import React, {useState, useEffect} from 'react';
import DoggoList from './DoggoList';
import RegisterButton from './RegisterButton.js';
import axios from 'axios';
import Registration from './Registration';

const apiUrl = 'https://dogfair.herokuapp.com/api/license_registrations'

function DoggoApp () {
  const [doggoData, setDoggoData] = useState ({data: []});
  const [isLoading, setIsLoading] = useState (true);

  // GET
  useEffect (() => {
    const fetchData = async () => {
      const result = await axios.get (apiUrl);
      setDoggoData (result.data);
      setIsLoading (false);
    };
    fetchData ();
  }, []);

  return (
    <div
      className="container"
      style={{paddingTop: '1em', paddingBottom: '1em'}}
    >
      <RegisterButton btnName="Register a new doggo" btnColor="is-info" />
      {/* <Registration /> */}

      {isLoading && <h1>loading data ....</h1>}

      {doggoData.data.map (doggo => (
        <DoggoList
          key={doggo.id}
          doggoName={doggo.name}
          doggoAge={doggo.age}
          doggoId={doggo.doggo_id.first + '...' + doggo.doggo_id.last}
          doggoDescription={doggo.description}
        />
      ))}

    </div>
  );
}

export default DoggoApp;
