import React from 'react';
import {Link} from 'react-router-dom';
import {useForm} from 'react-hook-form';

function Registration () {
  // // sending a post request
  // return (
  //   <div className="container">
  //     <div className="reg-container">
  //       <p>Doggo Registration</p>
  //       <div className="column">
  //         <input className="input" type="text" placeholder="Name" />
  //       </div>
  //       <div className="column">
  //         <input className="input" type="number" placeholder="Age" />
  //       </div>
  //       <div className="column">
  //         <input className="input" type="text" placeholder="Description" />
  //       </div>
  //       <div className="column">
  //         <input className="input" type="text" placeholder="Doggo id (Unique)" />
  //       </div>
  //       <Link to="/doggo-list"><button className="button is-primary">Submit</button></Link>
  //     </div>
  //   </div>
  // );

  const {handleSubmit, register, errors} = useForm ();
  const onSubmit = values => {
    console.log (values);
    const link = "./doggo-list"
  };

  return (
    <div className="container">
      <div className="reg-container">
        <p>Doggo Registration</p>
        <form onSubmit={handleSubmit (onSubmit)}>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Name"
              name="name"
              ref={register ({
                required: 'Name is required',
              })}
            />
            <p className="has-text-danger">
              {errors.name && errors.name.message}
            </p>
          </div>

          <div className="column">
            <input
              type="number"
              className="input"
              placeholder="Age"
              name="age"
              ref={register ({
                required: 'Age is required, must be within 0-100',
                min: 0,
                max: 100,
              })}
            />
            <p className="has-text-danger">
              {errors.age && errors.age.message}
            </p>
          </div>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Description"
              name="description"
              ref={register ({
                required: 'Description is required, up to 100 letters',
                maxLength: 100,
              })}
            />
            <p className="has-text-danger">
              {errors.description && errors.description.message}
            </p>
          </div>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Doggo id"
              name="doggoID"
              ref={register ({
                required: 'Doggo ID is required',
                pattern: {
                  value: /(J)(\d{6})([a-d])/g,
                  message: 'Invalid id format, starts with capital letter J, followed by any 6 digits number, and ends with any lowercase letter from a to d',
                },
              })}
            />
            <p className="has-text-danger">
              {errors.doggoID && errors.doggoID.message}
            </p>
          </div>

          <Link to={link}>
            <button className="button is-primary" type="submit">Submit</button>
          </Link>
        </form>
      </div>
    </div>
  );
}

export default Registration;
