import React, { useState, Fragment } from 'react'
import AdddoggoForm from './forms/AdddoggoForm'
import EditdoggoForm from './forms/EditdoggoForm'
import doggoTable from './tables/doggoTable'

const MainApp = () => {
	// Data
	const doggosData = [
		{ id: 1, name: 'Shiba inu', age: 2, description: 'also called doge', doggo_id: 'J123456c' },
		{ id: 2, name: 'Husky', age: 5, description: 'also called moon moon', doggo_id: 'J654321c' },
	]

	const initialFormState = { id: null, name: '', description: '' }

	// Setting state
	const [ doggos, setdoggos ] = useState(doggosData)
	const [ currentdoggo, setCurrentdoggo ] = useState(initialFormState)
	const [ editing, setEditing ] = useState(false)

	const adddoggo = doggo => {
		doggo.id = doggos.length + 1
		setdoggos([ ...doggos, doggo ])
	}

	const deletedoggo = id => {
		setEditing(false)

		setdoggos(doggos.filter(doggo => doggo.id !== id))
	}

	const updatedoggo = (id, updateddoggo) => {
		setEditing(false)

		setdoggos(doggos.map(doggo => (doggo.id === id ? updateddoggo : doggo)))
	}

	const editRow = doggo => {
		setEditing(true)

		setCurrentdoggo({ id: doggo.id, name: doggo.name, description: doggo.description })
	}

	return (
		<div className="container">
			<div className="flex-row">
				<div className="flex-large">
					{editing ? (
						<Fragment>
							<EditdoggoForm
								editing={editing}
								setEditing={setEditing}
								currentdoggo={currentdoggo}
								updatedoggo={updatedoggo}
							/>
						</Fragment>
					) : (
						<Fragment>
							<AdddoggoForm adddoggo={adddoggo} />
						</Fragment>
					)}
				</div>
				<div className="flex-large">
					<h2 style={{color:'white'}}>View doggos</h2>
					<doggoTable doggos={doggos} editRow={editRow} deletedoggo={deletedoggo} />
				</div>
			</div>
		</div>
	)
}

export default MainApp
