function useEndpoint(req) {
  const [res, setRes] = useState({
    data: null,
    complete: false,
    pending: false,
    error: false
  });
useEffect(
    () => {
      setRes({
        data: null,
        pending: true,
        error: false,
        complete: false
      });
      axios(req)
        .then(res =>
          setRes({
            data: res.data,
            pending: false,
            error: false,
            complete: true
          }),
        )
        .catch(() =>
          setRes({
            data: null,
            pending: false,
            error: true,
            complete: true
          }),
        );
    },
    [req.url]
  );
return res;
}
export default function DoggoApp() {
  const todosApi = "https://jsonplaceholder.typicode.com/todos";
  const [count, setCount] = useState(1);
  const todo = useEndpoint({
    method: "GET",
    url: `${todosApi}/${count}`
  });
return (
    <div>
      <button onClick={() => setCount(count + 1)}>Next Todo</button>
      <h1>Todo {count}</h1>
      <div>
        {(todo.pending && 'Loading...') ||
          (todo.complete && todo.data.title)
        }
      </div>
    </div>
  );
}
