import React, {useState, useEffect, Fragment} from 'react';
import axios from 'axios';
import DoggoList from './DoggoList';

import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

const apiUrl = 'http://dummy.restapiexample.com/api/v1/employees';

function AxiosTest (props) {
  const [doggoData, setDoggoData] = useState ({data: []});
  const [isLoading, setIsLoading] = useState (true);

  // GET
  useEffect (() => {
    const fetchData = async () => {
      const result = await axios.get (apiUrl);
      setDoggoData (result.data);
      setIsLoading (false);
    };
    fetchData ();
  }, []);

  return (
    <div
      className="container"
      style={{paddingTop: '1em', paddingBottom: '1em'}}
    >
      <p>Axios Test</p>

      {isLoading && <h1>loading data ....</h1>}

      {doggoData.data.map (doggo => (
        <Fragment>
          <DoggoList
            key={doggo.id}
            doggoName={doggo.employee_name}
            doggoAge={doggo.employee_salary}
            doggoId={doggo.employee_age}
            doggoDescription={doggo.profile_image}
          />
          <button>delete</button>
          <button>edit</button>
        </Fragment>
      ))}
    </div>
  );
}

function Post (props) {
  const [product, setProduct] = useState ({
    id: '',
    employee_name: '',
    employee_salary: '',
    employee_age: 0,
    profile_image: '',
  });
  const [showLoading, setShowLoading] = useState (false);
  const saveProduct = e => {
    setShowLoading (true);
    e.preventDefault ();
    const data = {
      employee_name: product.employee_name,
      employee_salary: product.employee_salary,
      employee_age: parseInt (product.employee_age),
      profile_image: product.profile_image,
    };
    axios
      .post (apiUrl, data)
      .then (result => {
        setShowLoading (false);
        props.history.push ('/show/' + result.data._id);
      })
      .catch (error => setShowLoading (false));
  };

  const onChange = e => {
    e.persist ();
    setProduct ({...product, [e.target.name]: e.target.value});
  };
  return (
    <Fragment>
      {showLoading && <p>loading...</p>}

      <Form onSubmit={saveProduct}>
        <Form.Group>
          <Form.Label>Product Name</Form.Label>
          <Form.Control
            type="text"
            name="prod_name"
            id="prod_name"
            placeholder="Enter product name"
            value={product.employee_name}
            onChange={onChange}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Product Description</Form.Label>
          <Form.Control
            as="textarea"
            name="prod_desc"
            id="prod_desc"
            rows="3"
            placeholder="Enter product description"
            value={product.employee_salary}
            onChange={onChange}
          />
        </Form.Group>
        <Form.Group>
          <Form.Label>Product Price</Form.Label>
          <Form.Control
            type="number"
            name="prod_price"
            id="prod_price"
            placeholder="Enter product price"
            value={product.employee_age}
            onChange={onChange}
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          Save
        </Button>
      </Form>
    </Fragment>
  );
}

export default AxiosTest;
