import React from 'react';
import {useHistory} from 'react-router-dom';
import {useForm} from 'react-hook-form';

function Registration (props) {
  const {handleSubmit, register, errors} = useForm ();
  const history = useHistory ();

  const onSubmit = values => {
    console.log (values);
    alert ('succesfully registered');
    history.push ('/');
  };

  return (
    <div className="container">
      <div className="reg-container">
        <p>Doggo Registration</p>
        <form onSubmit={handleSubmit (onSubmit)}>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Name"
              name="name"
              ref={register ({
                required: 'Name is required',
                pattern: {
                  value: '',
                  message: 'Please input valid name'
                }
              })}
            />
            <p className="has-text-danger">
              {errors.name && errors.name.message}
            </p>
          </div>

          <div className="column">
            <input
              type="number"
              className="input"
              placeholder="Age"
              name="age"
              ref={register ({
                required: 'Age is required',
                min: 0,
                max: 100,
                pattern: {
                  value: /[0-1]{1}[0-9]{0,2}/,
                  message: 'Age must be within 0-100'
                }
              })}
            />
            <p className="has-text-danger">
              {errors.age && errors.age.message}
            </p>
          </div>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Description"
              name="description"
              ref={register ({
                required: 'Description is required, up to 100 letters',
                maxLength: 100,
                pattern: {
                  value: '',
                  message: 'Description is only up to 100 letters'
                }
              })}
            />
            <p className="has-text-danger">
              {errors.description && errors.description.message}
            </p>
          </div>

          <div className="column">
            <input
              type="text"
              className="input"
              placeholder="Doggo id"
              name="doggoID"
              ref={register ({
                required: 'Doggo ID is required',
                pattern: {
                  value: /(J)(\d{6})([a-d])/g,
                  message: 'Invalid id format, starts with capital letter J, followed by any 6 digits number, and ends with any lowercase letter from a to d',
                },
              })}
            />
            <p className="has-text-danger">
              {errors.doggoID && errors.doggoID.message}
            </p>
          </div>

          <button className="button is-primary" type="submit">Submit</button>

        </form>
      </div>
    </div>
  );
}

export default Registration;
